import { useEffect, useContext } from "react";
import styled from "styled-components";

const TimerOptions = (props) => {
	let intervalId;

	const {
		isTimerActive,
		setIsTimerActive,
		hours,
		setHours,
		minutes,
		setMinutes,
		seconds,
		setSeconds,
	} = useContext(TimerContext);

	useEffect(() => {
		if (isTimerActive) {
			intervalId = setInterval(() => {
				handlePlay();
			}, 1000);
		}
	}, [isTimerActive]);

	// Play/pause
	const handlePlay = () => {
		// Проверяем кол-во секунд
		if (seconds <= 0) {
			// Проверяем кол-во минут
			if (minutes <= 0) {
				// Проверяем кол-во часов
				if (hours <= 0) {
					clearInterval(intervalId);
				} else {
					setHours(hours - 1);
					setMinutes(59);
				}
			} else {
				setMinutes(minutes - 1);
			}
			setSeconds(59);
		} else {
			setSeconds(seconds - 1);
		}
	};

	const playToggle = () => {
		props.setIsTimerActive(!props.isTimerActive);
		console.log(props.isTimerActive);

		// Если отсчет идет
		if (props.isTimerActive) {
			handlePlay();
			// intervalId = setInterval(() => {
			// 	handlePlay();
			// }, 1000);
		} else {
			// Останавливаем интервал
			clearInterval(intervalId);
		}
	};

	// Reset
	const handleReset = () => {
		// Останавливаем интервал
		clearInterval(intervalId);
		setIsTimerActive(false);

		setHours(startHours);
		setMinutes(startMinutes);
		setSeconds(setSeconds);
	};

	return (
		<StyledOptions>
			<div className="toggle-btns">
				<i
					className={
						"fa-solid fa-play" + (props.isTimerActive ? "is--hidden" : "")
					}
					id="play-btn"
					onClick={playToggle}
				></i>
				<i
					className={
						"fa-solid fa-pause" + (!props.isTimerActive ? "is--hidden" : "")
					}
					id="pause-btn"
					onClick={playToggle}
				></i>
			</div>
			<i
				onClick={handleReset}
				id="reset-btn"
				className="fa-solid fa-repeat"
			></i>
		</StyledOptions>
	);
};

export default TimerOptions;

const StyledOptions = styled.div`
	display: flex;
	gap: 1.5rem;

	i {
		font-size: 3rem;
		width: 3rem;
	}

	.toggle-btns {
		position: relative;
		width: 3rem;

		i {
			position: absolute;
			transition: opacity 0.2s ease-in-out;
		}
	}
`;
