import styled from "styled-components";

import TimerForm from "./Components/TimerForm";
import Timer from "./Components/Timer";

function App() {
	return (
		<StyledMain>
			<TimerForm></TimerForm>
			<Timer></Timer>
			<StyledOptions>
				<div className="toggle-btns">
					<div>
						<i id="play-btn" className="fa-solid fa-play"></i>
					</div>
					<div className="is--hidden">
						<i id="pause-btn" className="fa-solid fa-pause"></i>
					</div>
				</div>
				<i id="reset-btn" className="fa-solid fa-repeat"></i>
			</StyledOptions>
		</StyledMain>
	);
}

export default App;

const StyledMain = styled.main`
	width: 100vw;
	min-height: 100vh;
	display: flex;
	align-items: center;
	padding-top: 7rem;
	flex-direction: column;
	background-image: linear-gradient(120deg, #a8edea 0%, #fed6e3 60%);

	@media (max-width: 768px) {
		padding-top: 5rem;
	}
`;

const StyledTimer = styled.div`
	display: flex;
	margin: 5rem;

	@media (max-width: 768px) {
		margin: 3rem;
	}

	p {
		font-size: 6rem;
	}
`;

const StyledOptions = styled.div`
	display: flex;
	gap: 1.5rem;

	i {
		font-size: 3rem;
		width: 3rem;
	}

	.toggle-btns {
		position: relative;
		width: 3rem;

		i {
			position: absolute;
			transition: opacity 0.2s ease-in-out;
		}
	}
`;
