import React, { createContext, useState } from "react";

// Create a new context
export const TimerContext = createContext();

function TimerContextProvider(props) {
	// Set initial state values for seconds and minutes left
	const [secondsLeft, setSecondsLeft] = useState(60);
	const [minutesLeft, setMinutesLeft] = useState(25);

	return (
		// Pass the state values and setState functions as values to the context provider
		<TimerContext.Provider
			value={{ secondsLeft, setSecondsLeft, minutesLeft, setMinutesLeft }}
		>
			{props.children}
		</TimerContext.Provider>
	);
}

export default TimerContextProvider;
