import { useState, useContext, useEffect } from "react";

import { TimerContext } from "../TimerContextProvider";

import styled from "styled-components";

const TimerForm = () => {
	const [hoursInput, setHoursInput] = useState("");
	const [minutesInput, setMinutesInput] = useState("");
	const [secondsInput, setSecondsInput] = useState("");

	const [isInputError, setIsInputError] = useState(false);

	useEffect(() => {
		removeError();
	}, [hoursInput, minutesInput, secondsInput]);

	const {
		handleNumFormat,
		setStartHours,
		setStartMinutes,
		setStartSeconds,
		setHours,
		setMinutes,
		setSeconds,
	} = useContext(TimerContext);

	// Убрать сообщение об ошибке
	const removeError = () => {
		if (
			[hoursInput, minutesInput, secondsInput].every(
				(input) => Number(input) || input === ""
			)
		) {
			setIsInputError(false);
		} else {
			setIsInputError(true);
		}
	};

	// Связать инпут со стейтом
	const handleBinding = (e, newValue) => {
		const targetId = e.target.id.split("-")[0];

		switch (targetId) {
			case "hours":
				setHoursInput("");
				break;
			case "minutes":
				setMinutesInput("");
				break;
			case "seconds":
				setSecondsInput("");
				break;
		}
	};

	// Установка таймера
	const handleInputChange = (e) => {
		const targetId = e.target.id.split("-")[0];
		let newValue = e.target.value;

		switch (targetId) {
			case "hours":
				setHoursInput(newValue);
				break;
			case "minutes":
				setMinutesInput(newValue);
				break;
			case "seconds":
				setSecondsInput(newValue);
				break;
		}

		let isError = false;

		if (!Number(newValue) && Number(newValue) !== 0) {
			isError = true;
		} else {
			// Если значение инпута корректно, передаем его в таймер
			switch (targetId) {
				case "hours":
					newValue > 24
						? (isError = true)
						: setStartHours(handleNumFormat(newValue));
					break;
				case "minutes":
					newValue > 59
						? (isError = true)
						: setStartMinutes(handleNumFormat(newValue));
					break;
				case "seconds":
					newValue > 59
						? (isError = true)
						: setStartSeconds(handleNumFormat(newValue));
					break;
			}
		}

		if (!isError) {
			switch (targetId) {
				case "hours":
					setHours(newValue);
					break;
				case "minutes":
					setMinutes(newValue);
					break;
				case "seconds":
					setSeconds(newValue);
					break;
			}
		}
	};

	//Сброс инпута
	const handleInputClick = (e) => {
		e.target.value = "";
		const targetId = e.target.id.split("-")[0];

		switch (targetId) {
			case "hours":
				setHoursInput("");
				break;
			case "minutes":
				setMinutesInput("");
				break;
			case "seconds":
				setSecondsInput("");
				break;
		}
	};

	return (
		<StyledForm>
			<StyledInputContainer>
				<label htmlFor="hours-input">hh</label>
				<input
					value={hoursInput}
					onChange={handleInputChange}
					onClick={handleInputClick}
					type="text"
					id="hours-input"
				/>
			</StyledInputContainer>
			<StyledInputContainer>
				<label htmlFor="minutes-input">mm</label>
				<input
					value={minutesInput}
					onChange={handleInputChange}
					onClick={handleInputClick}
					type="text"
					id="minutes-input"
				/>
			</StyledInputContainer>
			<StyledInputContainer>
				<label htmlFor="seconds-input">ss</label>
				<input
					value={secondsInput}
					onChange={handleInputChange}
					onClick={handleInputClick}
					type="text"
					id="seconds-input"
				/>
			</StyledInputContainer>
			<StyledWarningMsg className={isInputError ? "" : "is--hidden"}>
				Пожалуйста, введите корректное значение
			</StyledWarningMsg>
		</StyledForm>
	);
};

export default TimerForm;

const StyledForm = styled.form`
	position: relative;
	width: 70vw;
	display: flex;
	justify-content: center;
	gap: 1.5rem;
	height: 10rem;
`;

const StyledInputContainer = styled.div`
	display: flex;
	flex-direction: column;
	align-items: center;

	label {
		font-size: 2rem;
		padding-bottom: 1rem;
	}

	input {
		min-height: 3rem;
		outline: none;
		padding: 0.5rem 1.5rem;
		text-align: center;
		font-size: 2rem;
		width: 100%;
		border: none;
		background: rgb(247, 245, 245);
	}
`;

const StyledWarningMsg = styled.p`
	position: absolute;
	text-align: center;
	width: 100%;
	bottom: 0;
	transition: opacity 0.2s ease-in-out;
	font-size: 1.8rem;
	font-weight: 300;

	@media (max-width: 768px) {
		font-size: 1rem;
	}
`;
