import { useState, useContext, useEffect } from "react";

import { TimerContext } from "../TimerContextProvider";

import styled from "styled-components";

const TimerForm = () => {
	const [hoursInput, setHoursInput] = useState("");
	const [hoursInputCorrect, setHoursInputCorrect] = useState(true);
	const [minutesInput, setMinutesInput] = useState("");
	const [minutesInputCorrect, setMinutesInputCorrect] = useState(true);
	const [secondsInput, setSecondsInput] = useState("");
	const [secondsInputCorrect, setSecondsInputCorrect] = useState(true);

	const [isInputError, setIsInputError] = useState(false);

	useEffect(() => {
		handleError();
	}, [hoursInput, minutesInput, secondsInput]);

	const {
		setStartHours,
		setStartMinutes,
		setStartSeconds,
		setHours,
		setMinutes,
		setSeconds,
	} = useContext(TimerContext);

	const checkInputs = (e) => {
		let newValue = e.target.value;
		switch (targetId) {
			case "hours":
				if (Number(newValue) && newValue <= 24) {
					setStartHours(newValue);
					setHours(newValue);
					setHoursInputCorrect(true);
				} else {
					setHoursInputCorrect(false);
				}
				setHoursInput(newValue);
				break;
			case "minutes":
				if (Number(newValue) && newValue <= 59) {
					setStartMinutes(newValue);
					setMinutes(newValue);
					setMinutesInputCorrect(true);
				} else {
					setMinutesInputCorrect(false);
				}
				setMinutesInput(newValue);
				break;
			case "seconds":
				if (Number(newValue) && newValue <= 59) {
					setStartSeconds(newValue);
					setSeconds(newValue);
					setSecondsInputCorrect(true);
				} else {
					setSecondsInputCorrect(true);
				}
				setSecondsInput(newValue);
				break;
		}
	};

	// Установка таймера
	const handleInputChange = (e) => {
		let newValue = e.target.value;
		checkInputs(e, targetId);
	};

	//Сброс инпута
	const handleInputClick = (e) => {
		e.target.value = "";
		//handleBinding(e, "");
	};

	return (
		<StyledForm>
			<StyledInputContainer>
				<label htmlFor="hours-input">hh</label>
				<input
					value={hoursInput}
					onChange={handleInputChange}
					onClick={handleInputClick}
					type="text"
					id="hours-input"
				/>
			</StyledInputContainer>
			<StyledInputContainer>
				<label htmlFor="minutes-input">mm</label>
				<input
					value={minutesInput}
					onChange={handleInputChange}
					onClick={handleInputClick}
					type="text"
					id="minutes-input"
				/>
			</StyledInputContainer>
			<StyledInputContainer>
				<label htmlFor="seconds-input">ss</label>
				<input
					value={secondsInput}
					onChange={handleInputChange}
					onClick={handleInputClick}
					type="text"
					id="seconds-input"
				/>
			</StyledInputContainer>
			<StyledWarningMsg className={isInputError ? "" : "is--hidden"}>
				Пожалуйста, введите корректное значение
			</StyledWarningMsg>
		</StyledForm>
	);
};

export default TimerForm;

const StyledForm = styled.form`
	position: relative;
	width: 70vw;
	display: flex;
	justify-content: center;
	gap: 1.5rem;
	height: 10rem;
`;

const StyledInputContainer = styled.div`
	display: flex;
	flex-direction: column;
	align-items: center;

	label {
		font-size: 2rem;
		padding-bottom: 1rem;
	}

	input {
		min-height: 3rem;
		outline: none;
		padding: 0.5rem 1.5rem;
		text-align: center;
		font-size: 2rem;
		width: 100%;
		border: none;
		background: rgb(247, 245, 245);
	}
`;

const StyledWarningMsg = styled.p`
	position: absolute;
	text-align: center;
	width: 100%;
	bottom: 0;
	transition: opacity 0.2s ease-in-out;
	font-size: 1.8rem;
	font-weight: 300;

	@media (max-width: 768px) {
		font-size: 1rem;
	}
`;
