import { useEffect, useContext } from "react";
import styled from "styled-components";

const TimerOptions = (props) => {
	let intervalId;

	const { isTimerActive, hours, minutes, seconds } = useContext(TimerContext);

	useEffect(() => {
		if (props.isTimerActive) {
			intervalId = setInterval(() => {
				handlePlay();
			}, 1000);
		}
	}, [isTimerActive]);

	// Play/pause
	const handlePlay = () => {
		console.log("play");
		// Проверяем кол-во секунд
		if (props.seconds <= 0) {
			// Проверяем кол-во минут
			if (props.minutes <= 0) {
				// Проверяем кол-во часов
				if (props.hours <= 0) {
					clearInterval(intervalId);
				} else {
					props.setHours(props.hours - 1);
					props.setMinutes(59);
				}
			} else {
				props.setMinutes(props.minutes - 1);
			}
			props.setSeconds(59);
		} else {
			props.setSeconds(props.seconds - 1);
		}
	};

	const playToggle = () => {
		props.setIsTimerActive(!props.isTimerActive);
		console.log(props.isTimerActive);

		// Если отсчет идет
		if (props.isTimerActive) {
			handlePlay();
			// intervalId = setInterval(() => {
			// 	handlePlay();
			// }, 1000);
		} else {
			// Останавливаем интервал
			clearInterval(intervalId);
		}
	};

	// Reset
	const handleReset = () => {
		// Останавливаем интервал
		clearInterval(intervalId);
		props.setIsTimerActive(false);

		props.setHours(props.startHours);
		props.setMinutes(props.startMinutes);
		props.setSeconds(props.setSeconds);
	};

	return (
		<StyledOptions>
			<div className="toggle-btns">
				<i
					className={
						"fa-solid fa-play" + (props.isTimerActive ? "is--hidden" : "")
					}
					id="play-btn"
					onClick={playToggle}
				></i>
				<i
					className={
						"fa-solid fa-pause" + (!props.isTimerActive ? "is--hidden" : "")
					}
					id="pause-btn"
					onClick={playToggle}
				></i>
			</div>
			<i
				onClick={handleReset}
				id="reset-btn"
				className="fa-solid fa-repeat"
			></i>
		</StyledOptions>
	);
};

export default TimerOptions;

const StyledOptions = styled.div`
	display: flex;
	gap: 1.5rem;

	i {
		font-size: 3rem;
		width: 3rem;
	}

	.toggle-btns {
		position: relative;
		width: 3rem;

		i {
			position: absolute;
			transition: opacity 0.2s ease-in-out;
		}
	}
`;
