import styled from "styled-components";

function App() {
	return (
		<StyledMain>
			<StyledForm action="">
				<StyledInputContainer>
					<label for="hours-input">hh</label>
					<input type="text" name="" id="hours-input" />
				</StyledInputContainer>
				<StyledInputContainer>
					<label for="minutes-input">mm</label>
					<input type="text" name="" id="minutes-input" />
				</StyledInputContainer>
				<StyledInputContainer>
					<label for="seconds-input">ss</label>
					<input type="text" name="" id="seconds-input" />
				</StyledInputContainer>
				<StyledWarningMsg class="is--hidden">
					Пожалуйста, введите корректное значение
				</StyledWarningMsg>
			</StyledForm>
			<StyledTimer>
				<p id="hours">01</p>
				<p>:</p>
				<p id="minutes">00</p>
				<p>:</p>
				<p id="seconds">00</p>
			</StyledTimer>
			<div class="options">
				<div class="toggle-btns">
					<div>
						<i id="play-btn" class="fa-solid fa-play"></i>
					</div>
					<div class="is--hidden">
						<i id="pause-btn" class="fa-solid fa-pause"></i>
					</div>
				</div>
				<i id="reset-btn" class="fa-solid fa-repeat"></i>
			</div>
		</StyledMain>
	);
}

export default App;

const StyledMain = styled.main`
	width: 100vw;
	min-height: 100vh;
	display: flex;
	align-items: center;
	padding-top: 7rem;
	flex-direction: column;
	background-image: linear-gradient(120deg, #a8edea 0%, #fed6e3 60%);
`;

const StyledForm = styled.form`
	position: relative;
	width: 70vw;
	display: flex;
	justify-content: center;
	gap: 1.5rem;
	height: 10rem;
`;

const StyledInputContainer = styled.div`
	display: flex;
	flex-direction: column;
	align-items: center;

	label {
		font-size: 2rem;
		padding-bottom: 1rem;
	}

	input {
		min-height: 3rem;
		outline: none;
		padding: 0.5rem 1.5rem;
		text-align: center;
		font-size: 2rem;
		width: 100%;
		border: none;
		background: rgb(247, 245, 245);
	}
`;

const StyledWarningMsg = styled.p`
	position: absolute;
	text-align: center;
	width: 100%;
	bottom: 0;
	transition: opacity 0.2s ease-in-out;
	font-size: 1.8rem;
	font-weight: 300;
`;

const StyledTimer = styled.div`
	display: flex;
	margin: 5rem;

	p {
		font-size: 6rem;
	}
`;
