import styled from "styled-components";

const Timer = (props) => {
	return (
		<StyledTimer>
			<p>{handleNumFormat(props.hours)}</p>
			<p>:</p>
			<p>{handleNumFormat(props.minutes)}</p>
			<p>:</p>
			<p>{handleNumFormat(props.seconds)}</p>
		</StyledTimer>
	);
};

export default Timer;

const StyledTimer = styled.div`
	display: flex;
	margin: 5rem;

	@media (max-width: 768px) {
		margin: 3rem;
	}

	p {
		font-size: 6rem;
	}
`;
