import { useContext } from "react";

import styled from "styled-components";

const Timer = (props) => {
	const {
		handleNumFormat,
		setStartHours,
		setStartMinutes,
		setStartSeconds,
		setHours,
		setMinutes,
		setSeconds,
	} = useContext(TimerContext);

	return (
		<StyledTimer>
			<p>{props.handleNumFormat(props.hours)}</p>
			<p>:</p>
			<p>{props.handleNumFormat(props.minutes)}</p>
			<p>:</p>
			<p>{props.handleNumFormat(props.seconds)}</p>
		</StyledTimer>
	);
};

export default Timer;

const StyledTimer = styled.div`
	display: flex;
	margin: 5rem;

	@media (max-width: 768px) {
		margin: 3rem;
	}

	p {
		font-size: 6rem;
	}
`;
