import { Context } from "./context";

import styled from "styled-components";

import TimerForm from "./Components/TimerForm";
import Timer from "./Components/Timer";
import TimerOptions from "./Components/TimerOptions";
import { useState } from "react";

function App() {
	// Приводим к корректному отображению
	const handleNumFormat = (num) => {
		return Number(num).toLocaleString(undefined, {
			minimumIntegerDigits: 2,
			useGrouping: false,
		});
	};

	return (
		<Context.Provider value={value}>
			<StyledMain>
				<TimerForm
					setStartHours={setStartHours}
					setStartMinutes={setStartMinutes}
					setStartSeconds={setStartSeconds}
					setIsInputError={setIsInputError}
					isInputError={isInputError}
					handleNumFormat={handleNumFormat}
					setHours={setHours}
					setMinutes={setMinutes}
					setSeconds={setSeconds}
				></TimerForm>
				<Timer
					handleNumFormat={handleNumFormat}
					hours={hours}
					minutes={minutes}
					seconds={seconds}
				></Timer>
				<TimerOptions
					seconds={seconds}
					minutes={minutes}
					hours={hours}
					setHours={setHours}
					setMinutes={setMinutes}
					setSeconds={setSeconds}
					isTimerActive={isTimerActive}
					setIsTimerActive={setIsTimerActive}
					startHours={startHours}
					startMinutes={startMinutes}
					startSeconds={startSeconds}
				></TimerOptions>
			</StyledMain>
		</Context.Provider>
	);
}

export default App;

const StyledMain = styled.main`
	width: 100vw;
	min-height: 100vh;
	display: flex;
	align-items: center;
	padding-top: 7rem;
	flex-direction: column;
	background-image: linear-gradient(120deg, #a8edea 0%, #fed6e3 60%);

	@media (max-width: 768px) {
		padding-top: 5rem;
	}
`;
