import styled from "styled-components";

const TimerForm = (props) => {
	// Установка таймера
	const handleInput = (e) => {
		const targetId = e.target.id.split("-")[0];
		let newValue = props.handleNumFormat(e.target.value);

		let isError = false;

		if (!Number(newValue) && Number(newValue) !== 0) {
			isError = true;
		} else {
			// Если значение инпута корректно, передаем его в таймер
			switch (targetId) {
				case "hours":
					newValue > 24 ? (isError = true) : props.setStartHours(newValue);
					break;
				case "minutes":
					newValue > 59 ? (isError = true) : props.setStarMinutes(newValue);
					break;
				case "seconds":
					newValue > 59 ? (isError = true) : props.setStartSeconds(newValue);
					break;
			}
		}

		if (isError) {
			setIsInputError(false);
			warningMsg.classList.remove("is--hidden");
		} else {
			removeError();
			document.querySelector(`#${targetId}`).innerHTML = newValue;
		}
	};

	return (
		<StyledForm>
			<StyledInputContainer>
				<label htmlFor="hours-input">hh</label>
				<input onChange={handleInput} type="text" name="" id="hours-input" />
			</StyledInputContainer>
			<StyledInputContainer>
				<label htmlFor="minutes-input">mm</label>
				<input type="text" name="" id="minutes-input" />
			</StyledInputContainer>
			<StyledInputContainer>
				<label htmlFor="seconds-input">ss</label>
				<input type="text" name="" id="seconds-input" />
			</StyledInputContainer>
			<StyledWarningMsg className="is--hidden">
				Пожалуйста, введите корректное значение
			</StyledWarningMsg>
		</StyledForm>
	);
};

export default TimerForm;

const StyledForm = styled.form`
	position: relative;
	width: 70vw;
	display: flex;
	justify-content: center;
	gap: 1.5rem;
	height: 10rem;
`;

const StyledInputContainer = styled.div`
	display: flex;
	flex-direction: column;
	align-items: center;

	label {
		font-size: 2rem;
		padding-bottom: 1rem;
	}

	input {
		min-height: 3rem;
		outline: none;
		padding: 0.5rem 1.5rem;
		text-align: center;
		font-size: 2rem;
		width: 100%;
		border: none;
		background: rgb(247, 245, 245);
	}
`;

const StyledWarningMsg = styled.p`
	position: absolute;
	text-align: center;
	width: 100%;
	bottom: 0;
	transition: opacity 0.2s ease-in-out;
	font-size: 1.8rem;
	font-weight: 300;

	@media (max-width: 768px) {
		font-size: 1rem;
	}
`;
