import styled from "styled-components";

const TimerOptions = (props) => {
	let intervalId;

	// Play/pause
	const handlePlay = () => {
		// Проверяем кол-во секунд
		if (props.seconds <= 0) {
			// Проверяем кол-во минут
			if (props.minutes <= 0) {
				// Проверяем кол-во часов
				if (props.hours <= 0) {
					clearInterval(intervalId);
				} else {
					props.setHours(props.hours - 1);
					props.setMinutes(59);
				}
			} else {
				props.setMinutes(props.minutes - 1);
			}
			props.setSeconds(59);
		} else {
			props.setSeconds(props.seconds - 1);
		}
	};

	const playToggle = () => {
		props.setIsTimerActive(!props.isTimerActive);

		// Если отсчет идет
		if (isTimerActive) {
			playBtn.parentNode.classList.add("is--hidden");
			pauseBtn.parentNode.classList.remove("is--hidden");

			handlePlay();
			intervalId = setInterval(() => {
				handlePlay();
			}, 1000);
		} else {
			// Останавливаем интервал
			clearInterval(intervalId);
		}
	};

	[playBtn, pauseBtn].forEach((btn) =>
		btn.addEventListener("click", playToggle)
	);

	// Reset
	const handleReset = () => {
		// Останавливаем интервал
		clearInterval(intervalId);
		isTimerActive = false;

		hours.innerHTML = startHours;
		minutes.innerHTML = startMinutes;
		seconds.innerHTML = startSeconds;
	};

	resetBtn.addEventListener("click", handleReset);

	return (
		<StyledOptions>
			<div className="toggle-btns">
				<i
					className={
						"fa-solid fa-pause" + (props.isTimerActive ? "is--hidden" : "")
					}
					id="play-btn"
				></i>
				<i
					className={
						"fa-solid fa-pause" + (!props.isTimerActive ? "is--hidden" : "")
					}
					id="pause-btn"
				></i>
			</div>
			<i id="reset-btn" className="fa-solid fa-repeat"></i>
		</StyledOptions>
	);
};

export default TimerOptions;

const StyledOptions = styled.div`
	display: flex;
	gap: 1.5rem;

	i {
		font-size: 3rem;
		width: 3rem;
	}

	.toggle-btns {
		position: relative;
		width: 3rem;

		i {
			position: absolute;
			transition: opacity 0.2s ease-in-out;
		}
	}
`;
