import styled from "styled-components";

const TimerForm = () => {
	return (
		<StyledForm>
			<StyledInputContainer>
				<label htmlFor="hours-input">hh</label>
				<input type="text" name="" id="hours-input" />
			</StyledInputContainer>
			<StyledInputContainer>
				<label htmlFor="minutes-input">mm</label>
				<input type="text" name="" id="minutes-input" />
			</StyledInputContainer>
			<StyledInputContainer>
				<label htmlFor="seconds-input">ss</label>
				<input type="text" name="" id="seconds-input" />
			</StyledInputContainer>
			<StyledWarningMsg className="is--hidden">
				Пожалуйста, введите корректное значение
			</StyledWarningMsg>
		</StyledForm>
	);
};

export default TimerForm;

const StyledForm = styled.form`
	position: relative;
	width: 70vw;
	display: flex;
	justify-content: center;
	gap: 1.5rem;
	height: 10rem;
`;

const StyledInputContainer = styled.div`
	display: flex;
	flex-direction: column;
	align-items: center;

	label {
		font-size: 2rem;
		padding-bottom: 1rem;
	}

	input {
		min-height: 3rem;
		outline: none;
		padding: 0.5rem 1.5rem;
		text-align: center;
		font-size: 2rem;
		width: 100%;
		border: none;
		background: rgb(247, 245, 245);
	}
`;

const StyledWarningMsg = styled.p`
	position: absolute;
	text-align: center;
	width: 100%;
	bottom: 0;
	transition: opacity 0.2s ease-in-out;
	font-size: 1.8rem;
	font-weight: 300;

	@media (max-width: 768px) {
		font-size: 1rem;
	}
`;
